mapper.insert:

 			INSERT INTO RCVAPPL(SSN, RCV_D, RCV_SEQ_NO, APPL_D, BIRTH_D, HG_NM, ENG_NM, STL_MTD, STL_ACT, BNK_CD, STL_DD,
							APPL_CLAS, STMT_SND_MTD, BILLADR_ADR1, BILLADR_ADR2, BILLADR_ZIP, HDP_NO, BRD, SCRT_NO, EMAIL_ADR,
							IMPSB_CLAS, IMPSB_CD, LST_OPR_TM, LST_OPR_D, LST_OPRT_EMPNO)
 			VALUES(#{SSN}
			,#{RCV_D}
			,RCV_SEQ_NO.nextval
			,#{APPL_D}
			,#{BIRTH_D}
			,#{HG_NM}
			,#{ENG_NM}
			,#{STL_MTD}
			,#{STL_ACT}
			,#{BNK_CD}
			,#{STL_DD}
			,#{APPL_CLAS}
			,#{STMT_SND_MTD}
			,#{BILLADR_ADR1}
			,#{BILLADR_ADR2}
			,#{BILLADR_ZIP}
			,#{HDP_NO}
			,#{BRD}
			,#{SCRT_NO}
			,#{EMAIL_ADR}
			,'0'
			,'00'
			,TO_CHAR(sysdate, 'HH24:MI')
			,TO_CHAR(sysdate, 'YYYYMMDD')
			,00000001
 		)


			INSERT INTO RCVAPPL(SSN, RCV_D, RCV_SEQ_NO, APPL_D, BIRTH_D, HG_NM, ENG_NM, STL_MTD, STL_ACT, BNK_CD, STL_DD,
							APPL_CLAS, STMT_SND_MTD, BILLADR_ADR1, BILLADR_ADR2, BILLADR_ZIP, HDP_NO, BRD, SCRT_NO, EMAIL_ADR,
							IMPSB_CLAS, IMPSB_CD, LST_OPR_TM, LST_OPR_D, LST_OPRT_EMPNO)
 			VALUES(#{SSN}
			,#{RCV_D}
			,RCV_SEQ_NO.nextval
			,#{APPL_D}
			,#{BIRTH_D}
			,#{HG_NM}
			,#{ENG_NM}
			,#{STL_MTD}
			,#{STL_ACT}
			,#{BNK_CD}
			,#{STL_DD}
			,#{APPL_CLAS}
			,#{STMT_SND_MTD}
			,#{BILLADR_ADR1}
			,#{BILLADR_ADR2}
			,#{BILLADR_ZIP}
			,#{HDP_NO}
			,#{BRD}
			,#{SCRT_NO}
			,#{EMAIL_ADR}
			,'1'
			,'01'
			,TO_CHAR(sysdate, 'HH24:MI')
			,TO_CHAR(sysdate, 'YYYYMMDD')
			,00000001
 		)

mapper.select:
SELECT * FROM RCVAPPL WHERE 1=1

	AND APPL_CLAS = #{APPL_CLAS}


	AND SSN = ${SSN}
